task main() {
    while (1 == 1) {
        if (SensorValue(SonarRight) < 14) {
            turnLEDOn(red);
        }
        if (SensorValue(SonarLeft) < 14) {
            turnLEDOn(green);
        }
        if (SensorValue(SonarRight) >= 14) {
            turnLEDOff(red);
        }
        if (SensorValue(SonarLeft) >= 14) {
            turnLEDOff(green);
        }
    }
}
